INSERT INTO `tongcheng_blg` VALUES ('23074', '139****4784', '//pic3.40017.cn/member/face/2015/08/27/17/sHIVcY.png', '中评; 有用:(0)', '饭菜真是没什么好吃的，里面还行，买了个2500的杯子说喝水对身体好，也不知道是否是真的，为了孩子尝', '2016-06-15', '2018-10-17 21:08:13', '24929');
