INSERT INTO `tongcheng_dxdt` VALUES ('45034', '185*****150', '//tcw-public.b0.upaiyun.com/headimg/facedefault.png', '好评; 有用:(0)', '景区整体给人感觉有山有水，环境宜人，可谓得天独厚。园内各项服务设施也比较齐全到位。不愧是5a级景点。', '2012-02-21', '2018-10-18 00:16:20', '6651');
