INSERT INTO `tongcheng_dxdt` VALUES ('45102', '151*****575', '//tcw-public.b0.upaiyun.com/headimg/facedefault.png', '好评; 有用:(0)', '同程很方便，优惠再多点就更好了。大小洞天风景不错，海一望无际，水很蓝，椰木很多，海风吹着很涼快', '2014-04-24', '2018-10-18 00:16:20', '6651');
