INSERT INTO `tongcheng_fjzd` VALUES ('48344', '159*****344', '//tcw-public.b0.upaiyun.com/headimg/facedefault.png', '中评; 有用:(0)', '景点很漂亮，但是海南物价就是宰人，没办法，没想法，钱花花的流出去！', '2012-02-05', '2018-10-18 00:52:28', '8142');
