INSERT INTO `tongcheng_fjzd` VALUES ('48346', '155imyqa165', '//pic3.40017.cn/member/face/2017/12/05/16/tcdefault.png', '好评; 有用:(0)', '水很清，风景很好！同程购票很方便。', '2015-04-14', '2018-10-18 00:52:28', '8142');
