INSERT INTO `tongcheng_fjzd` VALUES ('48366', '186*****641', '//tcw-public.b0.upaiyun.com/headimg/facedefault.png', '好评; 有用:(0)', '自驾出行比较方便，景点离市区偏远，景色很棒，值得一去。', '2014-02-22', '2018-10-18 00:52:28', '8142');
INSERT INTO `tongcheng_fjzd` VALUES ('48367', '138****0533', '//upload.17u.com/uploadfile/2005/11/08/2/boy.gif', '好评; 有用:(0)', '一大帮子人到的景点，取票很方便，小崔服务态度很好。上岛后人有点多，潜艇没有多大意思。', '2015-08-13', '2018-10-18 00:52:28', '8142');
