INSERT INTO `tongcheng_fjzd` VALUES ('48373', '135****0460', '//pic3.40017.cn/member/face/2015/08/27/17/9CS7ZO.png', '中评; 有用:(0)', '跟蜈岛比，人少了很多，没那么挤，海底运动不错的，风景感觉还是蜈好一些', '2016-06-13', '2018-10-18 00:52:28', '8142');
INSERT INTO `tongcheng_fjzd` VALUES ('48374', '189*****101', '//tcw-public.b0.upaiyun.com/headimg/facedefault.png', '好评; 有用:(0)', '去了一下，还可以，因为家里人去得多所以也没有在那里玩很久，只是呆了一个小时左右，总体还行', '2012-02-22', '2018-10-18 00:52:28', '8142');
