INSERT INTO `tongcheng_fjzd` VALUES ('48379', '135*****248', '//tcw-public.b0.upaiyun.com/headimg/facedefault.png', '好评; 有用:(0)', '取票耽误了一个多小时，同程网打了五六次电话和景区沟通，建议以后标明实名注册取票。', '2014-02-06', '2018-10-18 00:52:28', '8142');
