INSERT INTO `tongcheng_hsq` VALUES ('2664', '186****2660', '//pic3.40017.cn/member/face/2015/08/27/17/sHIVcY.png', '中评; 有用:(0)', '一分钱一分货，吹吹风也挺好的。', '2016-02-12', '2018-11-02 16:48:06', '26321');
