INSERT INTO `tongcheng_hsq` VALUES ('5779', 'cicigai', '//pic3.40017.cn/member/face/2015/08/27/17/sHIVcY.png', '好评; 有用:(0)', '号称是全国最大保存最完整的火山地质景观，景色很好，孩子很喜欢。极具特色，不同于以往的普通公园。游览感受很好。', '2017-01-23', '2018-11-02 19:37:40', '26321');
INSERT INTO `tongcheng_hsq` VALUES ('5780', '9F798350CD0D1650', '//pic3.40017.cn/member/face/2015/08/27/17/sHIVcY.png', '好评; 有用:(0)', '绿树成荫，空气清新，地质风貌独特，老妈很喜欢。感觉门票贵了一些。', '2017-02-06', '2018-11-02 19:37:40', '26321');
INSERT INTO `tongcheng_hsq` VALUES ('5781', '小小的maggie', '//pic3.40017.cn/member/face/2015/11/05/20/ktWaQX.jpg', '好评; 有用:(0)', '景区不算大，2小时就可以走到顶，不过都是步梯，感觉也挺辛苦的！', '2017-02-05', '2018-11-02 19:37:40', '26321');
INSERT INTO `tongcheng_hsq` VALUES ('5782', '白果树r', '//pic3.40017.cn/member/face/2017/12/05/16/tcdefault.png', '好评; 有用:(0)', '购票1小时以后入园，洞里凉快，顶尖部置高点观全景很美，很舒服，是享受。', '2018-01-18', '2018-11-02 19:37:40', '26321');
