INSERT INTO `tongcheng_nwhd` VALUES ('49091', 'memeonly0531', '//pic3.40017.cn/member/face/2015/08/31/14/jNDStn.png', '好评; 有用:(0)', '还可以。在同程买的143一人。到景区买是160。 缆车很过瘾。猴岛本身不大。一个小时能逛遍 小猴子很有意思', '2017-01-16', '2018-10-18 01:03:00', '734');
