INSERT INTO `tongcheng_tyhj` VALUES ('425', '同程会员_6F6AB89D28E', '//pic3.40017.cn/member/face/2017/12/05/16/tcdefault.png', '好评; 有用:(0)', '还不错，比想像中的好，景色怡人，恋人们更值得，鉴正爱情的地方。', '2017-08-20', '2018-10-17 17:52:56', '1370');
INSERT INTO `tongcheng_tyhj` VALUES ('429', '7E178398941B84F0', '//pic3.40017.cn/member/face/2015/08/27/17/sHIVcY.png', '好评; 有用:(0)', '同程订票很方便，不用取票刷码进很便捷，景点说实在的没有什么，当然要看个人，只是觉得比较有意义所以才去的', '2017-01-06', '2018-10-17 17:52:56', '1370');
