INSERT INTO `tongcheng_tyhj` VALUES ('431', '发条菱', '//pic3.40017.cn/member/face/2017/12/05/16/tcdefault.png', '好评; 有用:(0)', '景点的海景的确是非常完美的，石头那边人比较多，加上天气炎热，沙滩滚烫，随便拍了几张就撤出沙滩了。海边比较凉快，椰子树下吹吹海风，放松心情。', '2017-08-26', '2018-10-17 17:52:56', '1370');
INSERT INTO `tongcheng_tyhj` VALUES ('436', '清风幽幽', '//pic3.40017.cn/member/face/2015/11/25/08/Hb37gN.jpg', '好评; 有用:(0)', '购票方便快捷，取票速度快。走vip通道。景区风景优美。', '2018-08-24', '2018-10-17 17:52:56', '1370');
