INSERT INTO `tongcheng_wzz` VALUES ('39581', '134****3070', '//pic3.40017.cn/member/face/2016/05/05/02/BgTLlS.jpg', '好评; 有用:(0)', '毕竟来旅游了，还是要去一下的，团购划算一点点。猴子很可爱，听说会抢手机。', '2016-05-09', '2018-10-17 23:25:37', '9724');
