INSERT INTO `tongcheng_wzz` VALUES ('40179', '8CCC90BD3CBD3A6D', '//pic3.40017.cn/member/face/2017/12/05/16/tcdefault.png', '好评; 有用:(0)', '如果购买便宜的门票那就瞎看看，门票价格不值，只是爬到高处欣赏三亚的全景。', '2015-03-28', '2018-10-17 23:25:37', '9724');
