INSERT INTO `tongcheng_wzz` VALUES ('40287', '134*****246', '//tcw-public.b0.upaiyun.com/headimg/facedefault.png', '好评; 有用:(0)', '在同程网订票非常简洁方便，取票也很方便。鹿回头公园风景不错，可以360度观山下美景，有三亚市市区，有鱼村，有海洋。爱情主题鲜明，可以感受到浪漫的爱情气息。', '2014-01-30', '2018-10-17 23:25:37', '9724');
