INSERT INTO `tongcheng_wzz` VALUES ('40333', '155****8517', '//pic3.40017.cn/member/face/2015/08/27/17/sHIVcY.png', '好评; 有用:(0)', '都说鹿回头没有意思，可是我却要说到三亚一定要到鹿回头，景色美丽极了！在同程旅游订票方便快捷，最重要的是优惠很多啊！', '2016-02-17', '2018-10-17 23:25:37', '9724');
