INSERT INTO `tongcheng_wzz` VALUES ('40336', '同程网友', '//pic3.40017.cn/member/face/2016/01/23/10/h0ONTR.jpg', '好评; 有用:(0)', '观景台位置很好，可饱览三亚湾全景', '2016-01-25', '2018-10-17 23:25:37', '9724');
INSERT INTO `tongcheng_wzz` VALUES ('40337', '158****9333', '//pic3.40017.cn/member/face/2015/08/27/17/9CS7ZO.png', '好评; 有用:(0)', '如果无数次了。都是带家人朋友去的，觉得挺好的，还有猴子看，凤凰岛也可以看到，好评', '2016-02-17', '2018-10-17 23:25:37', '9724');
INSERT INTO `tongcheng_wzz` VALUES ('40339', 'fantasy_lwq', '//pic3.40017.cn/member/face/2017/12/05/16/tcdefault.png', '好评; 有用:(0)', '可以看到海景还不错。。电瓶车15分钟到公园顶。鹿回头主要也是看大雕塑。园区内椰子10元一个很好吃', '2015-01-30', '2018-10-17 23:25:37', '9724');
