INSERT INTO `tongcheng_wzz` VALUES ('40341', '139****7521', '//upload.17u.com/uploadfile/2005/11/08/2/boy.gif', '中评; 有用:(0)', '当晚停电！五点进景区，看了日落，夜景没看成！好可惜！居然没有后备发动电源', '2015-08-18', '2018-10-17 23:25:37', '9724');
INSERT INTO `tongcheng_wzz` VALUES ('40347', '158*****152', '//tcw-public.b0.upaiyun.com/headimg/facedefault.png', '好评; 有用:(0)', '鹿回头是个以爱情为主题的公园，风景很美，有许多热带的植物，在山顶可以观海凤凰岛的美景', '2013-03-07', '2018-10-17 23:25:37', '9724');
