INSERT INTO `tongcheng_ylw` VALUES ('13603', '6F3B44D028F83BA1', '//pic3.40017.cn/member/face/2017/12/05/16/tcdefault.png', '好评; 有用:(0)', '亚龙湾很美，好评是冲着亚龙湾的,森林公园本身一般，太多杂七杂八收费项目。', '2017-08-02', '2018-10-17 19:48:28', '26358');
INSERT INTO `tongcheng_ylw` VALUES ('13604', 'ljf2lx', '//pic3.40017.cn/member/face/2017/12/05/16/tcdefault.png', '中评; 有用:(0)', '实在一般，景点没两个，门票还那么贵。最坑的是索桥，排队排了一个多小时，然后索桥上全是人。', '2018-03-04', '2018-10-17 19:48:28', '26358');
INSERT INTO `tongcheng_ylw` VALUES ('13605', '7218152E36D0B591', '//pic3.40017.cn/member/face/2015/08/27/17/sHIVcY.png', '好评; 有用:(0)', '取票一定要下单后一个小时，出票速度很快，景区游览车坐起来蛮好玩，索桥也蛮好的，就是人太多，推荐看电影啊，看完电影基本上就算把景区游览了一次，虽然是动画的', '2017-04-23', '2018-10-17 19:48:28', '26358');
INSERT INTO `tongcheng_ylw` VALUES ('13606', '会飞的大熊猫', '//pic3.40017.cn/member/face/2015/08/27/17/sHIVcY.png', '好评; 有用:(0)', '观光车直接拉到景点，很便捷。公园里很凉爽，植物花卉都很漂亮，景观也不错，去的时候还遇到了一个剧组正在拍戏。公园是在山上，抵达山顶的时候可以俯视亚龙湾美景。', '2017-05-11', '2018-10-17 19:48:28', '26358');
INSERT INTO `tongcheng_ylw` VALUES ('13607', 'cainhyde', '//pic3.40017.cn/member/face/2017/12/05/16/tcdefault.png', '好评; 有用:(0)', '好评！门票在窗口一次取了，安检之类的都没问题。景区很大，景色不错，适合慢慢逛', '2017-08-01', '2018-10-17 19:48:28', '26358');
INSERT INTO `tongcheng_ylw` VALUES ('13609', '137qnhmj553', '//pic3.40017.cn/member/face/2015/08/27/17/sHIVcY.png', '好评; 有用:(0)', '家人带孩子去玩的，回家反应景色美极了，但人还是挺多的，景区调度很及时，孩子玩疯了', '2017-02-12', '2018-10-17 19:48:28', '26358');
INSERT INTO `tongcheng_ylw` VALUES ('13610', '神之裁罚', '//pic3.40017.cn/member/face/2016/08/12/09/vhX8SX.jpg', '好评; 有用:(0)', '挺有热带雨林的感觉。铁索桥那里真的很不错，就是人太多了。家人开心就是最大的满足。', '2017-03-10', '2018-10-17 19:48:28', '26358');
INSERT INTO `tongcheng_ylw` VALUES ('13612', '13631hrqhah', '//pic3.40017.cn/member/face/2015/08/27/17/sHIVcY.png', '好评; 有用:(0)', '网上订票的人超多，取票得排好长的队，景点不错，值得一去', '2017-02-10', '2018-10-17 19:48:28', '26358');
INSERT INTO `tongcheng_ylw` VALUES ('13614', '在心婉', '//pic5.40017.cn/01/000/8f/5c/rBLkBVlCRm-AAK1CAAEAAIgUC1g781.jpg', '好评; 有用:(0)', '亚龙湾森林公园很美，是非诚勿扰的拍摄地，可以在这从上到下看到大海，海也非常蓝，三亚的白云低低的，天空蓝蓝的，阳光暖暖的', '2017-06-15', '2018-10-17 19:48:28', '26358');
INSERT INTO `tongcheng_ylw` VALUES ('13615', '同程会员_18360D7565E', '//pic3.40017.cn/member/face/2017/12/05/16/tcdefault.png', '好评; 有用:(0)', '景区内线路规划挺好的 员工服务态度也很友好 区内三个点 离海湾远了点', '2017-06-25', '2018-10-17 19:48:28', '26358');
INSERT INTO `tongcheng_ylw` VALUES ('13617', '13855mucxdu', '//pic3.40017.cn/member/face/2017/12/05/16/tcdefault.png', '中评; 有用:(0)', '4a景区，说实话不太好，看不出热带雨林的特色，还是要感谢葛～～，完全靠一部电影带动了景区发展。提醒，那里的东西尽量多看少买！', '2017-08-14', '2018-10-17 19:48:28', '26358');
INSERT INTO `tongcheng_ylw` VALUES ('13618', '支付宝用户_604BB28D63', '//pic3.40017.cn/member/face/2017/02/04/21/gNeafp.jpg', '好评; 有用:(0)', '买完票一定要等一小时才能取票，也是醉醉的了！总体景点在山上，环境空气都挺好的！', '2017-02-09', '2018-10-17 19:48:28', '26358');
