INSERT INTO `tongcheng_ylw` VALUES ('13651', '13971inpbie', '//pic3.40017.cn/member/face/2015/08/27/17/sHIVcY.png', '好评; 有用:(0)', '买票和换票都很方便。景区还不错，但是热带雨林还是不去呀诺达。', '2017-02-09', '2018-10-17 19:48:28', '26358');
INSERT INTO `tongcheng_ylw` VALUES ('13652', '哈哈镜80', '//pic3.40017.cn/member/face/2017/12/05/16/tcdefault.png', '好评; 有用:(0)', '景点的游览车司机技术确实很好！景色不错，值得一去！', '2018-08-13', '2018-10-17 19:48:28', '26358');
