INSERT INTO `tongcheng_ylw` VALUES ('13658', '同程会员_D35AA48FD9D', '//pic3.40017.cn/member/face/2017/12/05/16/tcdefault.png', '好评; 有用:(0)', '购票一个小时后才能取票，建议提前安排好时间，避免到了景区取不出票。吊桥不长，人挤人，也不会有恐惧感', '2018-05-04', '2018-10-17 19:48:28', '26358');
INSERT INTO `tongcheng_ylw` VALUES ('13659', 'D553C5E38BF7E8AA', '//pic3.40017.cn/member/face/2015/08/27/17/sHIVcY.png', '好评; 有用:(0)', '人少索道没有那么多的人，可以好好的拍照，单独收费项目多', '2017-01-24', '2018-10-17 19:48:28', '26358');
INSERT INTO `tongcheng_ylw` VALUES ('13660', '柠檬清', '//pic3.40017.cn/member/face/2016/08/11/15/kca3Ey.jpg', '好评; 有用:(0)', '排队取票半个小时，过索道排队一个半小时，整体风景不错，就是人太多了。', '2017-02-03', '2018-10-17 19:48:28', '26358');
