INSERT INTO `tongcheng_ylw` VALUES ('17444', '13817jykhva', '//pic3.40017.cn/member/face/2017/12/05/16/tcdefault.png', '中评; 有用:(0)', '比现场买便宜，不过没有多大意思，就是一个山，分了五个景点，人工痕迹太重，女儿喜欢椰子奶冻，坐下边休息边吃边吹山风，还行', '2015-04-13', '2018-10-17 19:48:28', '26358');
INSERT INTO `tongcheng_ylw` VALUES ('17446', '138*****784', '//tcw-public.b0.upaiyun.com/headimg/facedefault.png', '好评; 有用:(0)', '景区很漂亮，站在千里伞下真的有一目千里的感觉，海岸线上还有岛屿，旁边就有鸟巢，太美了！感觉很不错。只是三亚很宰人，一瓶水就要十元啊！', '2012-05-06', '2018-10-17 19:48:28', '26358');
