INSERT INTO `tongcheng_ylw` VALUES ('17680', '550BDB72DC4208EB', '//pic3.40017.cn/member/face/2017/12/05/16/tcdefault.png', '好评; 有用:(0)', '景色很不错，特别站在最高处远眺亚龙湾海天一色感觉很是优美，下次再来一定到这边住一晚！', '2015-07-03', '2018-10-17 19:48:28', '26358');
