INSERT INTO `tongcheng_ylw` VALUES ('17735', '189*****170', '//tcw-public.b0.upaiyun.com/headimg/facedefault.png', '好评; 有用:(0)', '天热氧吧，很棒!花了20元排队20分钟走了传说中的索道，140米长，很多明星都走过，感觉还行', '2014-02-07', '2018-10-17 19:48:28', '26358');
INSERT INTO `tongcheng_ylw` VALUES ('17736', '135****5302', '//pic3.40017.cn/member/face/2015/08/31/14/jNDStn.png', '好评; 有用:(0)', '坐着电瓶车绕来绕去欣赏热带雨林风光，在山顶俯瞰亚龙湾美景。自然风光够好，去不去走吊桥都行。', '2015-12-12', '2018-10-17 19:48:28', '26358');
