INSERT INTO `tongcheng_ysdwy` VALUES ('899', '151*****712', '//tcw-public.b0.upaiyun.com/headimg/facedefault.png', '好评; 有用:(0)', '做的公交车，大巴。车行区看不到什么东西，也不让下车喂大象，还是要自驾才好。步行区感觉还不错。各种大型小型的动物', '2013-05-31', '2018-11-02 16:36:46', '16965');
