INSERT INTO `xiecheng_blg` VALUES ('12127', '快乐小猪', 'https://images4.c-ctrip.com/target/t1/headphoto/909/573/332/72d6fa521173442295b365344dec3806_C_180_180.jpg', '', '赶着假期过来感受一下黎族的风土人情，停车场爆满，人满为患，他们热情的招待每一位顾客，一路参观黎村的文化底蕴，看着老奶奶们的纯手工制作编织服装，美食更是爱不释手。很满意的一次旅行。 展开更多', '2018-02-17', '2018-10-17 08:48:48', '2018-02-17 详情 举报 有用 （0）', '有用 （0）', '107588', '90751');
