INSERT INTO `xiecheng_blg` VALUES ('12180', '走南逛北', 'https://images4.c-ctrip.com/target/Z8090e00000075km8BB28_C_180_180.jpg', '', '黎族村寨还不错苗族村寨不怎么样，去景点的大路上沿路有卖各种芒果的 景点里边的烤五脚猪肉比景点出口处的价格高了一倍。香蕉不贵又好吃 展开更多', '2018-03-10', '2018-10-17 08:48:48', '2018-03-10 详情 举报 有用 （0）', '有用 （0）', '107588', '90751');
