INSERT INTO `xiecheng_blg` VALUES ('12413', 'xiaowen717', 'https://images4.c-ctrip.com/target/t1/headphoto/177/080/529/285e9abeede347d9a7c1cc5e1b4c9287_C_180_180.jpg', '', '发展的比较成熟的民族村。对自然以及文化的保护和传承很值得北方其他古村学习。可能和民族特性有关吧。这个景点不累。比起天涯海角和南山等这里不是太大。行程从进村开始都有本村导游带领。感觉有趣又轻松。很值得一看的。表演也推荐看一下。另外景区有购物但也绝不是强买强卖什么的。很适合报个半日游参观一下。 展开更多', '2018-03-31', '2018-10-17 08:48:48', '2018-03-31 详情 举报 有用 （2）', '有用 （2）', '107588', '90751');
INSERT INTO `xiecheng_blg` VALUES ('12414', 'E35****09', 'https://images4.c-ctrip.com/target/t1/headphoto/295/828/780/6e6c2151ed65497c850945d5d2b702ae_C_180_180.jpg', '', '值得来的景区。游人比较多，带老人推轮椅有些高的地方没有去。园区大部分服务人员态度很好！个别地方无障碍通道标示不清，不停地问，服务人员都一一回答。遇到有少量台阶他们还主动帮忙搬抬轮椅，非常感谢！最后从甘什村下来游览车一直把我们送到景区门口。一次不错的旅行体验！ 展开更多', '2018-03-12', '2018-10-17 08:48:48', '2018-03-12 详情 举报 有用 （0）', '有用 （0）', '107588', '90751');
