INSERT INTO `xiecheng_boao` VALUES ('5816', 'chendi7143', 'https://images4.c-ctrip.com/target/t1/headphoto/177/080/529/285e9abeede347d9a7c1cc5e1b4c9287_C_180_180.jpg', '', '非常喜欢 非常惊叹 每个平凡人都得以体验名人们的体验 看着人们纷纷就坐贵宾席 元首席留影留念 心里很感激博鳌的存在 琼海因博鳌而闻名 也因博鳌而备受瞩目 提点建议 管理下交通 私家车泛滥还都是滴滴打车的 下单9.9司机电话过来说 我不能按滴滴收费 你要交给我 而且要60！！后来坐了公交车 6元5角到了博鳌 到了博鳌烟消云散！气势磅礴！国际范儿！一点不后悔！ 展开更多', '2018-02-12', '2018-11-05 09:24:11', '2018-02-12 详情 举报 有用 （0）', '有用 （0）', '54550', '84907');
