INSERT INTO `xiecheng_ddh` VALUES ('35299', '雅雅呀雅', 'https://images4.c-ctrip.com/target/fd/headphoto/g4/M06/E9/C9/CggYHVXIczKADPbFAAAiSWh1s-E067_C_180_180.jpg', '景色：5  趣味：5  性价比：5   ', '大东海是三亚海湾中离市中心最近的一个湾，市区东行10分钟车程便可到达，因此交通和吃东西都相对比较便利。虽然沙质和水质比不上亚龙湾，但是比三亚湾要好，加上地理位置和酒店价位，成了高性价比的所在。 展开更多', '2017-01-09', '2018-10-17 08:48:48', '2017-01-09 详情 举报 有用 （2）', '有用 （2）', '3224', '10558611');
