INSERT INTO `xiecheng_ddh` VALUES ('35301', 'M12****488', 'https://images4.c-ctrip.com/target/t1/headphoto/882/320/089/158cae5ddb6143c0b6ae18523614cf08_C_180_180.jpg', '景色：4  趣味：3  性价比：3   ', '风景挺漂亮的，海水很蓝，游泳区分成几块，游泳水温刚好，水质很好，浪大以后就不让下水了。所有附加服务都要收费，除了上卫生间。我们现在去是淡季，人还不算多，总体来说还可以呢。 展开更多', '2016-09-13', '2018-10-17 08:48:48', '2016-09-13 详情 举报 有用 （3）', '有用 （3）', '3224', '10558611');
