INSERT INTO `xiecheng_ddh` VALUES ('35317', 'christinayiyan', 'https://images4.c-ctrip.com/target/t1/headphoto/322/494/110/c0482089b0ff49dd88b4b151dbc367be_C_180_180.jpg', '景色：4  趣味：4  性价比：4   ', '非常的美與安靜！著名文華東方酒店就扎根在榆海路。大東海水質特別清淨。附近的夏日商場以及大菠蘿都值得一去 展开更多', '2016-02-05', '2018-10-17 08:48:48', '2016-02-05 详情 举报 有用 （4）', '有用 （4）', '3224', '10558611');
