INSERT INTO `xiecheng_ddh` VALUES ('35789', 'lily_dongyw', 'https://images4.c-ctrip.com/target/headphoto/934/802/023/fb0aea99b76e447a995bcae8c8719a7a_C_180_180.jpg', '景色：2  趣味：2  性价比：2   ', '对于没见过大海的也许有惊喜，但是现在临近春节的原因 加上外地人来此避东的越来越多，且好多素质不高的，大东海和三亚湾海边都跟煮饺子似的，垃圾也很多，海水很脏。不如稍远点的海边。三亚沿海建的都不错，现在亚龙湾都很脏了。只要中国人聚堆的地方都差不多。海天一色只能远观。 展开更多', '2015-02-12', '2018-10-17 08:48:48', '2015-02-12 详情 举报 有用 （2）', '有用 （2）', '3224', '10558611');
