INSERT INTO `xiecheng_ddh` VALUES ('35890', 'sev****ye', 'https://images4.c-ctrip.com/target/t1/headphoto/911/829/330/50c237658c6844279ead5df767354876_C_180_180.jpg', '景色：4  趣味：5  性价比：4    建议游览1天', '大东海，真心很方便，这边住宿的地方很多，旅店离海边很近，可以直接穿泳装下到大东海浴场，而且这边的大东海广场是大型集散地，基本每个旅行社都会到这里接人，很是便利，而且大东海的海滩也很适合游泳，沙子比亚龙湾差点，但比三亚湾要好不少。 展开更多', '2015-01-08', '2018-10-17 08:48:48', '2015-01-08 详情 举报 有用 （1）', '有用 （1）', '3224', '10558611');
