INSERT INTO `xiecheng_ddh` VALUES ('35997', '170****8728', 'https://images4.c-ctrip.com/target/t1/headphoto/397/132/243/542067aa29904ae9aca7e88f43eaeb6f_C_180_180.jpg', '景色：5  趣味：5  性价比：5    建议游览2小时', '最后一天才好好的看了一眼大东海，发现大东海也蛮美的，整个海湾成一个半圆形，海水很清，那天上午天空正好有云彩，阳光照耀的海域是天蓝色的，云彩遮挡的地方是深蓝色的，然后下午就来了毛哥家的海鲜店，招待我们点了菜。点了辣炒和乐蟹，海胆蒸蛋，清蒸石斑，椒盐虾，竹蛏子，龙虾，蒜蓉粉丝蒸扇贝，满满一桌子 都很满意，环境不错 服务态度超好！地理位置也很好！ 展开更多', '2016-10-14', '2018-10-17 08:48:48', '2016-10-14 详情 举报 有用 （0）', '有用 （0）', '3224', '10558611');
