INSERT INTO `xiecheng_dsl` VALUES ('7292', '_WeCh****77813', 'https://images4.c-ctrip.com/target/t1/headphoto/491/056/464/eb3c7472323e4c3598f58ffa8a6fce99_C_180_180.jpg', '景色：4  趣味：4  性价比：4   ', '东山岭遍山都是奇石，有块石头就是“一线天”胜景之处的风动石，它重达百余吨，能在海风的吹拂和人力的推摇下产生晃动。东山岭濒临南海，景色十分秀丽。岭上还有多处古迹和庙宇，均为省重点保护文物，其中潮音寺尤为海外侨胞重视。 展开更多', '2015-02-02', '2018-11-05 09:24:11', '2015-02-02 详情 举报 有用 （8）', '有用 （8）', '3199', '75966');
