INSERT INTO `xiecheng_dxdt` VALUES ('27145', '罗泽尔左宗棠', 'https://images4.c-ctrip.com/target/t1/headphoto/491/056/464/eb3c7472323e4c3598f58ffa8a6fce99_C_180_180.jpg', '', '超棒！必须给满分，感觉性价比很高，游人不多，还有博物馆可以看，满满的道家文化，作为中医学子真是亲切，感觉不用坐观光车，一步一景，下午将近傍晚去的，夕阳无限好，海波荡漾，当年鉴真登岸的时候不知道是不是跟我看到的风景一样呢？ 展开更多', '2018-06-18', '2018-10-17 08:48:48', '2018-06-18 详情 举报 有用 （0）', '有用 （0）', '3228', '75994');
