INSERT INTO `xiecheng_dxdt` VALUES ('27494', 'M34****382', 'https://images4.c-ctrip.com/target/t1/headphoto/164/424/952/04eab99bd26f47acadef4fd3f95b9f37_C_180_180.jpg', '', '去的时候天气多云，不晒。在携程买的半价老人学生票65，味道餐厅的海鲜自助餐票58点评返10，现场98的，省了不少，当天有椒盐海中虾 烤肠 红油海参 时蔬 豇豆腊肉 西红柿炒鸡蛋等热菜 ，豆丝 木耳 花生等凉菜， 海螺汤 皮蛋粥 例汤 绿豆汤回个汤，米饭 水饺 凉面等主食，黄金饼 面包片等小吃，芒果汁 仙人掌汁等饮料。我们是坐电瓶车到小月湾，从里往外步行游览的，一路行来，椰风海浪，礁石群里螃蟹爬，不老松实为龙血树，寿比南山寓意好，仙梯直达试剑峰，延寿静心桥相连，行气玉佩铭留足，百岁阶上走一走，玄妙阁前有龙王，博物馆里瞧史前，来到味道观海景，值得细细品味！ 展开更多', '2018-02-28', '2018-10-17 08:48:48', '2018-02-28 详情 举报 有用 （2）', '有用 （2）', '3228', '75994');
