INSERT INTO `xiecheng_fjzd` VALUES ('30106', '人人都爱处女座', 'https://images4.c-ctrip.com/target/Z80v0w000000kod3g87AD_C_180_180.jpg', '', '位于海南陵水县东北部海上，岛上有众多珍稀植物和动物。此岛海水清澈可见度达5~10米，岸上观看可将海水的颜色分为3层分别为碧蓝、碧绿、深蓝，因此这还是潜水的好去处。 展开更多', '2017-12-14', '2018-10-17 08:48:48', '2017-12-14 详情 举报 有用 （2）', '有用 （2）', '63528', '10758497');
