INSERT INTO `xiecheng_fjzd` VALUES ('30161', 'joeviqueen', 'https://images4.c-ctrip.com/target/Z8090q000000g9q021D86_C_180_180.jpg', '景色：5  趣味：4  性价比：5   ', '绝对值 虽然不知道以后人为会不会造成海水变质 但是现在去绝对赞 海岛1/3是水上项目 还有海狮表演不过要另外收费 翻过山就是另一幅美景 碧海蓝天 岩石悬崖 美不胜收 这次游岛很值得 但愿保持这份自然 展开更多', '2017-12-11', '2018-10-17 08:48:48', '2017-12-11 详情 举报 有用 （2）', '有用 （2）', '63528', '10758497');
