INSERT INTO `xiecheng_fjzd` VALUES ('30251', '_WeCh****82206', 'https://images4.c-ctrip.com/target/Z80n0l000000cuip7BB08_C_180_180.jpg', '景色：5  趣味：5  性价比：5   ', '很值得大家去玩，那里的风景特别好，水很蓝很蓝，推荐大家玩，玩跟我老公去的，那里工作人员蛮好的，地方有点偏远，不过有很多免费的大巴车接送还是很方便的，以后还会去玩的 展开更多', '2017-05-03', '2018-10-17 08:48:48', '2017-05-03 详情 举报 有用 （3）', '有用 （3）', '63528', '10758497');
