INSERT INTO `xiecheng_fjzd` VALUES ('30452', '_WeCh****923369', 'https://images4.c-ctrip.com/target/t1/headphoto/207/494/838/fa687736cf3f484480526cfc0eb6c073_C_180_180.jpg', '', '之前去了未开发的加井岛，对于分界洲岛抱着走走逛逛的心态，然而让我眼前一亮。女朋友还被选上去舞台同海狮表演了。分界洲岛比蜈支洲岛，个人认为舒服得多，环岛爬山看表演，舒适许多。虽然商业化，但也比较让游客舒心。 展开更多', '2018-09-17', '2018-10-17 08:48:48', '2018-09-17 详情 举报 有用 （0）', '有用 （0）', '63528', '10758497');
