INSERT INTO `xiecheng_fjzd` VALUES ('30454', '_M13****1038', 'https://images4.c-ctrip.com/target/t1/headphoto/441/333/730/ffac788ed0594b848a0771564fe6a2ee_C_180_180.jpg', '', '非常不错，去的时候天气不好，感觉色彩不够美，到了岛上太阳出来就色彩斑斓了，虽然人气不如蜈支洲岛，但各方面的评价绝对更高，满足你对海岛的一切需求，海洋动物看了个遍，潜水虽不是那么彩色透彻，但在国内算是一流了，下次一定选择岛上独栋半山别墅入住。 展开更多', '2018-05-06', '2018-10-17 08:48:48', '2018-05-06 详情 举报 有用 （0）', '有用 （0）', '63528', '10758497');
