INSERT INTO `xiecheng_hsdzgy` VALUES ('4285', '天天开心的可乐人', 'https://images4.c-ctrip.com/target/t1/headphoto/057/777/943/0b93f8268d5546308915f4f9fcaa9483_C_180_180.jpg', '景色：5  趣味：5  性价比：5   ', '景色很好，服务人员态度也好号，价钱也便宜，值得一游。火山口环境优美，树木茂盛，很值得大家欣赏，现在海口制高点，鸟瞰海口，又是一种心情。 展开更多', '2018-01-12', '2018-11-02 16:12:03', '2018-01-12 详情 举报 有用 （4）', '有用 （4）', '3175', '75945');
