INSERT INTO `xiecheng_hsdzgy` VALUES ('4341', 'M22****7722', 'https://images4.c-ctrip.com/target/t1/headphoto/258/512/704/6bce7506eecc48f8a4a6d3177e0acf24_C_180_180.jpg', '', '地质公园里热带植物品种比较多，我第一次看到了菠萝蜜高挂树上，长了很多“胡须”的榕树，杨桃树等等，里面很适合夏天出游，高大的树木郁郁葱葱，人们漫步其间，丝毫不觉炎热。 展开更多', '2018-07-07', '2018-11-02 16:12:03', '2018-07-07 详情 举报 有用 （0）', '有用 （0）', '3175', '75945');
