INSERT INTO `xiecheng_hsdzgy` VALUES ('4423', '塔河胡杨', 'https://images4.c-ctrip.com/target/t1/headphoto/857/872/237/edc9e981b00e436a8dcee595001d62fa_C_180_180.jpg', '', '挺好，看火山看植物，有科普有风景，比五大连池火山公园好太多太多。0百度地图太坑，直接导航检票处，找停车场停车从检票地返回去买票要走200米多。到了售票处才发现……不说了都是泪。记住雷琼火山地址公园就是火山公园，导航搜索雷琼就好，该死火山公园显示的是火山口的位置。 展开更多', '2018-04-15', '2018-11-02 16:12:03', '2018-04-15 详情 举报 有用 （0）', '有用 （0）', '3175', '75945');
