INSERT INTO `xiecheng_lht` VALUES ('21620', '可乐痘痘', 'https://images4.c-ctrip.com/target/t1/headphoto/882/320/089/158cae5ddb6143c0b6ae18523614cf08_C_180_180.jpg', '', '首先可以俯瞰三亚的全景，这个是最最重要的！还可以看日落（因为我是下午去的）景色秀丽，满满的绿意盎然，走到哪里都是一片风景 展开更多', '2017-11-04', '2018-10-17 08:48:48', '2017-11-04 详情 举报 有用 （0）', '有用 （0）', '3230', '75996');
INSERT INTO `xiecheng_lht` VALUES ('21621', '_WeCh****03591', 'https://images4.c-ctrip.com/target/t1/headphoto/699/910/854/683dab66bb374136af9930ea204dfc7e_C_180_180.jpg', '', '很漂亮的景区，不枉此行！白天看凤凰岛美景，晚上领略三亚晚霞风情。距离大东海很近，步行到达，方便的很。门票价格相对其他景区，很优惠。强烈推荐，提前网购景点门票，快捷还优惠，哈哈！70岁以上老人免门票，重点强调下。 展开更多', '2017-11-01', '2018-10-17 08:48:48', '2017-11-01 详情 举报 有用 （0）', '有用 （0）', '3230', '75996');
