INSERT INTO `xiecheng_lht` VALUES ('21753', 'M22****612', 'https://images4.c-ctrip.com/target/Z80l0c00000064axj1E84_C_180_180.jpg', '', '鹿回头公园设施完善，电瓶车直接到山顶，适合老人和小孩子很周到，一点不累，还可以滑滑梯1分钟到电瓶车处，安全可靠。上顶望到全三亚景观，太美了。 展开更多', '2018-04-05', '2018-10-17 08:48:48', '2018-04-05 详情 举报 有用 （0）', '有用 （0）', '3230', '75996');
