INSERT INTO `xiecheng_lht` VALUES ('22335', '_WeCh****558996', 'https://images4.c-ctrip.com/target/t1/headphoto/491/056/464/eb3c7472323e4c3598f58ffa8a6fce99_C_180_180.jpg', '', '白天去的。景色宜人。看别人说半小时爬上去，我们爬了1个小时。热带地区的植物北方见得少，所以景色很值得看。观景平台可以看到三亚地标建筑5只眼以及整个三亚湾。上去的时候看见有人喂鸽子。下山的时候看见有人在喂猴子。还不错。 展开更多', '2018-06-27', '2018-10-17 08:48:48', '2018-06-27 详情 举报 有用 （0）', '有用 （0）', '3230', '75996');
